import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Home.css';
import Products from './Products';

const Home = ({cart,setCart, handleAddToCart}) => {
  const [recommendations, setRecommendations] = useState([]);
  const [topRated, setTopRated] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();
  const [sw,usesw] = useState(0);

  const userId = localStorage.getItem("user_id")

  const [relatedProducts, setRelatedProducts] = useState([]);

    const fetchRelatedProducts = async () => {
        try {
            const userId = localStorage.getItem('user_id');
            if (!userId) return;

            const response = await fetch(`http://localhost:5000/recommend-by-brand?user_id=${userId}`);
            const data = await response.json();

            if (response.ok) {
              console.log(data)
                const relatedItems = data?.slice(0,15)
                setRelatedProducts(relatedItems);
            } else {
                console.error("Error fetching related products:", data.error);
            }
        } catch (error) {
            console.error("Error fetching related products:", error);
        }
    };

    // Fetch related products whenever the cart changes
    useEffect(() => {
            fetchRelatedProducts();
    }, [])

  const getValidImageUrl = (imageUrl) => {
    const imageUrls = imageUrl.split('|').map(url => url.trim());
    for (const url of imageUrls) {
      if (url) return url;
    }
    return 'https://via.placeholder.com/150'; 
  };

  useEffect(() => {
    const fetchCollaborativeRecommendations = async () => {
      const userId = localStorage.getItem('user_id');
      if (!userId) return;

      setLoading(true);
      setError('');

      try {
        const { data } = await axios.get(`http://localhost:5000/recommendations?user_id=${userId}`);
        const filteredRecommendations = data.filter(rec => rec.Rating > 0.0);
        setRecommendations(filteredRecommendations);
      } catch (error) {
        setError('Failed to fetch recommendations. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    const fetchTopRatedProducts = async () => {
      setLoading(true);
      setError('');
      
      try {
        const { data } = await axios.get('http://localhost:5000/rating-recommendation');
        setTopRated(data);
      } catch (error) {
        setError('Failed to fetch top-rated products. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchCollaborativeRecommendations();
    fetchTopRatedProducts();
  }, []);

  const handleProductClick = (productId) => {
    if (productId) {
      navigate(`/product/${productId}`);
    } else {
      console.log('No valid product ID');
    }
  };

  const fetchCart = async () => {
    try {
      const userId = localStorage.getItem('user_id');

        if (!userId) return;

        const response = await fetch(`http://localhost:5000/get_cart?user_id=${userId}`);
        const data = await response.json();

        if (response.ok) {
            setCart(data.cart);  // Update cart state
        } else {
            console.error("Error fetching cart:", data.error);
        }
    } catch (error) {
        console.error("Error fetching cart:", error);
    }
};

// Call fetchCart when the component mounts
useEffect(() => {
    fetchCart();
}, []);

  const handleCardClick = (product) => {
    console.log(product);
    // Navigate to product detail page and pass product data
    navigate(`/product-detail?query=${product.Name}`, { state: { product } });
  };

  return (
    <div className="home-container">
      <div><div className='bg-green-300 p-4'>relprod</div><div>topprod</div></div>
      {
        userId && 
          <>
          <h1>Products similar to you liked</h1>
          {
            <Products items={relatedProducts} handleAddToCart={handleAddToCart} fetchCart={fetchCart} handleClick={()=>{}}/>
          }
          </>
        
      }
            <hr className="section-divider" />
      <h1>Recommended Products for You</h1>
      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}
      {recommendations.length === 0 && !loading && !error && (
        <p>No recommendations found</p>
      )}

        {<Products items={recommendations} handleAddToCart={handleAddToCart} fetchCart={fetchCart} handleClick={()=>{}}/>}

      <hr className="section-divider" />

      <h1>Top-Rated Products</h1>
      <div>
      {topRated.length === 0 && !loading && !error && (
        <p>No top-rated products found</p>
      )}
      </div>
        {<Products items={topRated} handleAddToCart={handleAddToCart} fetchCart={fetchCart} handleClick={()=>{}}/>}

      <hr className="section-divider" />
      <footer className="footer">
        <p>&copy; 2025 Rahilkhan Recommendation system . All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
