import React, { useEffect, useState } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import './ProductDetail.css';

const ProductDetail = () => {
  const location = useLocation();
  const { product } = location.state || {};
  const [similarProducts, setSimilarProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  if (!product) {
    return <p>Product not found</p>;
  }

  const fetchSimilarProducts = async () => {
    setLoading(true);
    setError('');
    try {
      const { data } = await axios.get(
        `http://localhost:5000/similar-products?category=${encodeURIComponent(product.Category)}`
      );
      setSimilarProducts(data);
    } catch (err) {
      setError('Failed to fetch similar products. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchSimilarProducts();
  }, [product.Category]);

  const getValidImageUrl = (imageUrl) => {
    const imageUrls = imageUrl.split('|').map((url) => url.trim());
    for (const url of imageUrls) {
      if (url) return url;
    }
    return 'https://via.placeholder.com/150';
  };

  return (
    <div className="product-detail-container">
      <div className="product-detail">
        <div className="image">
          <img
            src={product.ImageURL}
            alt={product.Name}
            className="product-detail-image"
          />
          <button className="btn" id="addtoCart">Add to Cart</button>
          <button className="btn" id="buyNow">Buy Now</button>
        </div>

        <div className="product-detail-info">
          <h2>{product.Name}</h2>
          <p>{product.Brand}</p>
          <p>Rating: {product.Rating} ‚≠ê</p>
          <p>{product.ReviewCount} reviews</p>
          <p>{product.Description}</p>
        </div>
      </div>

      <h3>Similar Products</h3>
      {loading && <p>Loading similar products...</p>}
      {error && <p className="error">{error}</p>}
      <div className="similar-products-container">
        {similarProducts.map((similarProduct, index) => (
          <div key={index} className="similar-product-card">
            <img
              src={getValidImageUrl(similarProduct.ImageURL)}
              alt={similarProduct.Name}
              className="similar-product-image"
            />
            <div className="similar-product-info">
              <h4>{similarProduct.Name}</h4>
              <p>{similarProduct.Brand}</p>
              <p>Rating: {similarProduct.Rating} ‚≠ê</p>
              <p>{similarProduct.ReviewCount} reviews</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ProductDetail;
