import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Home.css';

const Home = ({cart,setCart}) => {
  const [recommendations, setRecommendations] = useState([]);
  const [topRated, setTopRated] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const userId = localStorage.getItem("user_id")

  const [relatedProducts, setRelatedProducts] = useState([]);

    const fetchRelatedProducts = async () => {
        try {
            const userId = localStorage.getItem('user_id');
            if (!userId) return;

            const response = await fetch(`http://localhost:5000/recommend-by-brand?user_id=${userId}`);
            const data = await response.json();

            if (response.ok) {
              console.log(data)
                setRelatedProducts(data.related_products);
            } else {
                console.error("Error fetching related products:", data.error);
            }
        } catch (error) {
            console.error("Error fetching related products:", error);
        }
    };

    // Fetch related products whenever the cart changes
    useEffect(() => {
            fetchRelatedProducts();
    }, [])

  const getValidImageUrl = (imageUrl) => {
    const imageUrls = imageUrl.split('|').map(url => url.trim());
    for (const url of imageUrls) {
      if (url) return url;
    }
    return 'https://via.placeholder.com/150'; 
  };

  useEffect(() => {
    const fetchCollaborativeRecommendations = async () => {
      const userId = localStorage.getItem('user_id');
      if (!userId) return;

      setLoading(true);
      setError('');

      try {
        const { data } = await axios.get(`http://localhost:5000/recommendations?user_id=${userId}`);
        const filteredRecommendations = data.filter(rec => rec.Rating > 0.0);
        setRecommendations(filteredRecommendations);
      } catch (error) {
        setError('Failed to fetch recommendations. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    const fetchTopRatedProducts = async () => {
      setLoading(true);
      setError('');
      
      try {
        const { data } = await axios.get('http://localhost:5000/rating-recommendation');
        setTopRated(data);
      } catch (error) {
        setError('Failed to fetch top-rated products. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchCollaborativeRecommendations();
    fetchTopRatedProducts();
  }, []);

  const handleProductClick = (productId) => {
    if (productId) {
      navigate(`/product/${productId}`);
    } else {
      console.log('No valid product ID');
    }
  };

  const fetchCart = async () => {
    try {
      const userId = localStorage.getItem('user_id');

        if (!userId) return;

        const response = await fetch(`http://localhost:5000/get_cart?user_id=${userId}`);
        const data = await response.json();

        if (response.ok) {
            setCart(data.cart);  // Update cart state
        } else {
            console.error("Error fetching cart:", data.error);
        }
    } catch (error) {
        console.error("Error fetching cart:", error);
    }
};

// Call fetchCart when the component mounts
useEffect(() => {
    fetchCart();
}, []);


  const handleAddToCart = async (product) => {
    try {
         // Retrieve the logged-in user ID
         const userId = localStorage.getItem('user_id');
        if (!userId) {
            alert("Please log in first!");
            return;
        }

        const response = await fetch("http://localhost:5000/add_to_cart", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                user_id: userId,
                product: product, // Send full product object
            }),
        });

        const data = await response.json();

        if (response.ok) {
            alert(data.message);
            fetchCart(); // Refresh cart after adding
        } else {
            alert(`Error: ${data.error}`);
        }
    } catch (error) {
        console.error("Error adding product to cart:", error);
    }
};

  const handleCardClick = (product) => {
    console.log(product);
    // Navigate to product detail page and pass product data
    navigate(`/product-detail?query=${product.Name}`, { state: { product } });
  };

  return (
    <div className="home-container">
      {
        userId && 
          <>
          <h1>Products similar to you liked</h1>
          {relatedProducts?.length > 0 && (
                <div className="related-products-section">
                    <h2>Products You May Like</h2>
                    <div className="product-grid">
                        {relatedProducts.map((item) => (
                            <div key={item.ProdID} className="product-card">
                                <img src={getValidImageUrl(item.ImageURL)} alt={item.Name} className="product-image" />
                                <div className="product-info">
                                    <h2>{item.Name}</h2>
                                    <p>{item.Brand}</p>
                                    <p>Rating: {item.Rating} ⭐</p>
                                    <p>{item.ReviewCount} reviews</p>
                                    <button className="add-to-cart-button">Add to Cart</button>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>)}
          </>
        
      }
      <hr />
      <h1>Recommended Products for You</h1>
      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}
      {recommendations.length === 0 && !loading && !error && (
        <p>No recommendations found</p>
      )}

      <div className="recommendation-container" >
        {recommendations.map((rec) => (
          <div 
            key={rec.ProdID} 
            className="recommendation-card"
          >
            <img 
              src={getValidImageUrl(rec.ImageURL)} 
              alt={rec.Name} 
              className="product-image" 
            />
            <div className="product-info">
              <h2>{rec.Name}</h2>
              <p>{rec.Brand}</p>
              <p>Rating: {rec.Rating} ⭐</p>
              <p>{rec.ReviewCount} reviews</p>
              <button className="add-to-cart-button" onClick={()=>{handleAddToCart(rec)}}> Add to Cart</button>
            </div>
          </div>
        ))}
      </div>

      <hr className="section-divider" />

      <h1>Top-Rated Products</h1>
      {topRated.length === 0 && !loading && !error && (
        <p>No top-rated products found</p>
      )}
      <div className="recommendation-container">
        {topRated.map((rec, index) => (
          <div 
            key={index} 
            className="recommendation-card"
            onClick={() => handleProductClick(rec.ProdID)}
          >
            <img 
              src={getValidImageUrl(rec.ImageURL)} 
              alt={rec.Name} 
              className="product-image" 
            />
            <div className="product-info">
              <h2>{rec.Name}</h2>
              <p>{rec.Brand}</p>
              <p>Rating: {rec.Rating} ⭐</p>
              <p>{rec.ReviewCount} reviews</p>
              <button className="add-to-cart-button">Add to Cart</button>
            </div>
          </div>
        ))}
      </div>

      <hr className="section-divider" />
      <footer className="footer">
        <p>&copy; 2025 Rahilkhan Recommendation system . All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
