import { useNavigate } from "react-router-dom";

export default function Products({ items, handleAddToCart, handleClick }) {
  const getValidImageUrl = (imageUrl) => {
    const imageUrls = imageUrl?.split('|').map(url => url.trim()).filter(Boolean) || [];
    return imageUrls[0] || 'https://via.placeholder.com/300x300?text=No+Image';
  };

  return (
    <div className="products-container">
      {items.length > 0 ? (
        <div className="product-grid">
          {items.map((item) => (
            <div 
              key={item.ProdID} 
              className="product-card"
              onClick={() => handleClick(item)}
            >
              <div className="product-image-container">
                <img 
                  src={getValidImageUrl(item.ImageURL)} 
                  alt={item.Name} 
                  className="product-image"
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/300x300?text=No+Image';
                  }}
                />
                <div className="product-badge">{item.Brand}</div>
              </div>
              
              <div className="product-details">
                <h3 className="product-title">{item.Name}</h3>
                
                <div className="product-rating">
                  <div className="stars">
                    {Array(5).fill().map((_, i) => (
                      <span 
                        key={i} 
                        className={`star ${i < Math.floor(item.Rating) ? 'filled' : ''}`}
                      >
                        {i < Math.floor(item.Rating) ? '★' : '☆'}
                      </span>
                    ))}
                  </div>
                  <span className="rating-value">{item.Rating.toFixed(1)}</span>
                  <span className="review-count">({item.ReviewCount} reviews)</span>
                </div>
                
                <button 
                  className="add-to-cart-button"
                  onClick={(e) => {
                    e.stopPropagation();
                    handleAddToCart(item);
                  }}
                >
                  <span>Add to Cart</span>
                  <svg xmlns="http://www.w3.org/2000/svg" className="cart-icon" viewBox="0 0 20 20" fill="currentColor">
                    <path d="M3 1a1 1 0 000 2h1.22l.305 1.222a.997.997 0 00.01.042l1.358 5.43-.893.892C3.74 11.846 4.632 14 6.414 14H15a1 1 0 000-2H6.414l1-1H14a1 1 0 00.894-.553l3-6A1 1 0 0017 3H6.28l-.31-1.243A1 1 0 005 1H3zM16 16.5a1.5 1.5 0 11-3 0 1.5 1.5 0 013 0zM6.5 18a1.5 1.5 0 100-3 1.5 1.5 0 000 3z" />
                  </svg>
                </button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="no-products">
          <p>No products found</p>
        </div>
      )}
    </div>
  );
}
