import React, { useState } from 'react';
import axios from 'axios';
import './Navbar.css'; // Include styling here

const Navbar = () => {
  const [productName, setProductName] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const [isModalOpen, setIsModalOpen] = useState(false);
  const [formType, setFormType] = useState(''); // To track whether it's 'Sign In' or 'Log In'

  const fetchRecommendations = async () => {
    setLoading(true);
    setError('');
    try {
      const { data } = await axios.get(`http://localhost:5000/content-recommendation?item_name=${productName}`);
      if (Array.isArray(data)) {
        setRecommendations(data);
      } else {
        setError('Unexpected data format');
      }
    } catch (error) {
      setError('Failed to fetch recommendations. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    if (productName.trim()) {
      fetchRecommendations();
    }
  };

  const toggleModal = (type) => {
    setFormType(type);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setFormType('');
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const url = formType === 'Sign In' ? '/signup' : '/login';
    
    try {
      const response = await axios.post(`http://localhost:5000${url}`, {
        email: formData.get('email'),
        password: formData.get('password')
      });
      console.log(response.data); // Handle success
      closeModal();
    } catch (err) {
      console.error(err); // Handle error
    }
  };

  return (
    <div>
      <nav className="navbar">
        <div className="navbar-brand">
          <a href="/">Home</a>
        </div>
        <div className="search-bar-container">
          <input
            type="text"
            value={productName}
            onChange={(e) => setProductName(e.target.value)}
            placeholder="Search for products"
            className="search-input"
          />
          <button onClick={handleSearch} disabled={loading} className="search-button">
            {loading ? 'Loading...' : 'Search'}
          </button>
        </div>
        <div className="navbar-links">
          <a href="/cart">Cart</a>
          {/* Sign In / Log In Button with Dropdown */}
          <div className="dropdown">
            <button className="dropbtn">Account</button>
            <div className="dropdown-content">
              <button onClick={() => toggleModal('Sign In')}>Sign In</button>
              <button onClick={() => toggleModal('Log In')}>Log In</button>
            </div>
          </div>
        </div>
      </nav>

      {recommendations.length > 0 && (
        <div className="recommendation-container">
          {recommendations.map((rec) => (
            <div key={rec.Name} className="recommendation-card">
              <img src={rec.ImageURL} alt={rec.Name} className="product-image" />
              <div className="product-info">
                <h2>{rec.Name}</h2>
                <p>{rec.Brand}</p>
                <p>Rating: {rec.Rating} ⭐</p>
                <p>{rec.ReviewCount} reviews</p>
                <button className="add-to-cart-button">Add to Cart</button>
              </div>
            </div>
          ))}
        </div>
      )}

      {error && <p className="error">{error}</p>}

      {/* Modal for Sign In / Log In */}
      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <span className="close" onClick={closeModal}>&times;</span>
            <h2>{formType}</h2>
            <form onSubmit={handleFormSubmit}>
              <div>
                <label>Email:</label>
                <input type="email" name="email" required />
              </div>
              <div>
                <label>Password:</label>
                <input type="password" name="password" required />
              </div>
              <button type="submit">{formType}</button>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Navbar;
