import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import "./Navbar.css";

const Navbar = () => {
  const [productName, setProductName] = useState("");
  const [userEmail, setUserEmail] = useState(localStorage.getItem("userEmail") || "");
  const [showBrands, setShowBrands] = useState(false);
  const [brands, setBrands] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch brands from backend
    fetch("http://localhost:5000/brands")
      .then((response) => response.json())
      .then((data) => setBrands(data))
      .catch((error) => console.error("Error fetching brands:", error));
  }, []);

  const handleSearch = () => {
    if (productName.trim()) {
      navigate(`/search?query=${encodeURIComponent(productName)}`);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userEmail");
    setUserEmail("");
    navigate("/");
  };

  const getEmailDetails = (email) => {
    if (!email) return { initial: "", displayName: "" };
    const firstLetter = email[0].toUpperCase();
    const name = email.split("@")[0];
    return { initial: firstLetter, displayName: name };
  };

  const { initial, displayName } = getEmailDetails(userEmail);

  const handleBrandClick = (brand) => {
    navigate(`/products?brand=${encodeURIComponent(brand)}`);
  };

  return (
    <div>
      <nav className="navbar">
        <div className="navbar-brand">
          <a href="/">Home</a>
        </div>

        {/* Brand Dropdown */}
        <div
          className="brand-dropdown"
          onMouseEnter={() => setShowBrands(true)}
          onMouseLeave={() => setShowBrands(false)}
        >
          <button className="brand-button">Brands â–¼</button>
          {showBrands && (
            <ul className="dropdown-menu">
              {brands.map((brand, index) => (
                <li key={index} onClick={() => handleBrandClick(brand)}>
                  {brand}
                </li>
              ))}
            </ul>
          )}
        </div>

        <div className="search-bar-container">
          <input
            type="text"
            value={productName}
            onChange={(e) => setProductName(e.target.value)}
            placeholder="Search for products"
            className="search-input"
          />
          <button onClick={handleSearch} className="search-button">
            Search
          </button>
        </div>

        <div className="navbar-links">
          <a href="/cart">Cart</a>
          {userEmail ? (
            <div className="user-info">
              <div className="user-icon">{initial}</div>
              <button onClick={handleLogout} className="logout-button">
                Logout
              </button>
            </div>
          ) : (
            <a href="/account">Account</a>
          )}
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
