import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './HomePage.css'; // Import the CSS file

const HomePage = () => {
    const [recommendations, setRecommendations] = useState([]);
    const userId = localStorage.getItem('userId'); // Get user_id from localStorage

    useEffect(() => {
        const fetchRecommendations = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/home?user_id=${userId}`);
                setRecommendations(response.data);
            } catch (error) {
                console.error("Error fetching recommendations:", error);
            }
        };

        if (userId) {
            fetchRecommendations();
        }
    }, [userId]);

    return (
        <div className="home-container">
            <h1>Recommended for You</h1>
            <div className="recommendation-container">
                {recommendations.length > 0 ? (
                    recommendations.map((item) => (
                        <div className="recommendation-card" key={item.ProdID}>
                            <img src={item.ImageURL} alt={item.Name} className="product-image" />
                            <div className="product-info">
                                <h3>{item.Name}</h3>
                                <p>Brand: {item.Brand}</p>
                                <p>Rating: {item.Rating} ‚≠ê</p>
                                <p>Reviews: {item.ReviewCount}</p>
                                <button className="add-to-cart-button">Add to Cart</button>
                            </div>
                        </div>
                    ))
                ) : (
                    <p>No recommendations available.</p>
                )}
            </div>
        </div>
    );
};

export default HomePage;
