import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Home.css';  // Create CSS for styling the product cards

const Home = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCollaborativeRecommendations = async () => {
      const userId = localStorage.getItem('user_id'); // Get the user_id from localStorage
      if (!userId) return;

      setLoading(true);
      setError('');

      try {
        const { data } = await axios.get(`http://localhost:5000/recommendations?user_id=${userId}`);
        setRecommendations(data);
      } catch (error) {
        setError('Failed to fetch recommendations. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchCollaborativeRecommendations();
  }, []);

  return (
    <div className="home">
        <h1>Recommended Products</h1>
        <div className="recommendation-container">
            {recommendedProducts.map((rec) => (
                <div key={rec.ProdID} className="recommendation-card" onClick={() => handleCardClick(rec.ProdID)}>
                    <img src={rec.ImageURL} alt={rec.Name} className="product-image" />
                    <div className="product-info">
                        <h2>{rec.Name.length > 20 ? `${rec.Name.substring(0, 20)}...` : rec.Name}</h2>
                        <p>{rec.Brand}</p>
                        <p>Rating: {rec.Rating} ‚≠ê</p>
                        <p>{rec.ReviewCount} reviews</p>
                        <button className="add-to-cart-button">Add to Cart</button>
                    </div>
                </div>
            ))}
        </div>
    </div>
);
};


export default Home;
