import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import './Navbar.css';

const Navbar = () => {
  const [productName, setProductName] = useState('');
  const [userEmail, setUserEmail] = useState(null); // State for user's email
  const navigate = useNavigate(); // Initialize useNavigate

  const handleSearch = () => {
    if (productName.trim()) {
      // Redirect to the search results page with the search query
      navigate(`/search?query=${encodeURIComponent(productName)}`);
    }
  };

  const handleLogout = () => {
    setUserEmail(null); // Clear user email on logout
    navigate('/'); // Redirect to home after logout
  };

  const renderAccountLink = () => {
    if (userEmail) {
      const firstLetter = userEmail.charAt(0).toUpperCase(); // First letter of email
      const userName = userEmail.split('@')[0]; // Get name from email (excluding domain)

      return (
        <div className="account-info" onClick={handleLogout} style={{ cursor: 'pointer' }}>
          <div className="user-circle">{firstLetter}</div>
          <span>{userName}</span>
          <button onClick={handleLogout} className="logout-button">Logout</button>
        </div>
      );
    } else {
      return <a href="/account">Account</a>;
    }
  };

  return (
    <div>
      <nav className="navbar">
        <div className="navbar-brand">
          <a href="/">Home</a>
        </div>
        <div className="search-bar-container">
          <input
            type="text"
            value={productName}
            onChange={(e) => setProductName(e.target.value)}
            placeholder="Search for products"
            className="search-input"
          />
          <button onClick={handleSearch} className="search-button">
            Search
          </button>
        </div>
        <div className="navbar-links">
          <a href="/cart">Cart</a>
          {renderAccountLink()}
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
