import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Cart.css';

const Cart = ({cart,setCart}) => {
    // const [cart, setCart] = useState([]);

    // useEffect(() => {
    //     const storedCart = JSON.parse(localStorage.getItem('cart')) || [];
    //     setCart(storedCart);
    // }, []);

    // const handleRemoveFromCart = (productId) => {
    //     const updatedCart = cart.filter(item => item.ProdID !== productId);
    //     setCart(updatedCart);
    //     localStorage.setItem('cart', JSON.stringify(updatedCart));
    // };

    const getValidImageUrl = (imageUrl) => {
        console.log(imageUrl)
        const imageUrls = imageUrl?.split('|').map(url => url.trim());
        for (const url of imageUrls) {
          if (url) return url;
        }
        return 'https://via.placeholder.com/150'; 
      };

    const navigate = useNavigate();

    const fetchCart = async () => {
        try {
          const userId = localStorage.getItem('user_id');
    
            if (!userId) return;
    
            const response = await fetch(`http://localhost:5000/get_cart?user_id=${userId}`);
            const data = await response.json();
    
            if (response.ok) {
                setCart(data.cart);  // Update cart state
            } else {
                console.error("Error fetching cart:", data.error);
            }
        } catch (error) {
            console.error("Error fetching cart:", error);
        }
    };

    const handleRemoveFromCart = async (productId, productName, action = '') => {
        try {
            const userId = localStorage.getItem("user_id");
    
            if (!userId) {
                alert("Please log in first!");
                return;
            }
    
            const item = cart.find(item => item.Name === productName);
            if (!item) return;
    
            let updatedCart;
    
            if (action === 'remove') {
                updatedCart = cart.filter(item => item.Name !== productName);
                setCart(updatedCart);
            } else {
                if (item.count > 1) {
                    updatedCart = cart.map(item =>
                        item.Name === productName ? { ...item, count: item.count - 1 } : item
                    );
                    setCart(updatedCart);
                } else {
                    updatedCart = cart.filter(item => item.Name !== productName);
                    setCart(updatedCart);
                }
            }
    
            const response = await fetch("http://localhost:5000/remove_from_cart", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    user_id: userId,
                    product_id: productId,
                    product_name: productName,
                    action: action || 'decrease'  // <-- always send an action
                }),
            });
    
            const data = await response.json();
            if (!response.ok) {
                alert(`Error: ${data.error}`);
            }
    
            fetchCart();
        } catch (error) {
            console.error("Error removing product from cart:", error);
        }
    };
    
    
    const handleIncreaseCount = async (productId,productName) => {
        try {
            const userId = localStorage.getItem("user_id");
    
            if (!userId) {
                alert("Please log in first!");
                return;
            }
    
            const updatedCart = cart.map(item =>
                item.Nane === productName ? { ...item, count: item.count + 1 } : item
            );
    
            setCart(updatedCart);
    
            // Update database
            const response = await fetch("http://localhost:5000/increase_cart_count", {
                method: "POST",
                headers: {
                    "Content-Type": "application/json",
                },
                body: JSON.stringify({
                    user_id: userId,
                    product_id: productId,
                    product_name:productName
                }),
            });
    
            const data = await response.json();
    
            if (!response.ok) {
                alert(`Error: ${data.error}`);
            }

            fetchCart()
        } catch (error) {
            console.error("Error increasing product count:", error);
        }
    };
    
    
    // Call fetchCart when the component mounts
    useEffect(() => {
        fetchCart();
    }, []);

    return (
        <div className="cart-container">
            <h1>Your Cart</h1>
            {cart.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                <div className="product-grid">
                    {cart.map((item) => (
                        <div key={item.ProdID} className="product-card">
                            <img src={getValidImageUrl(item.ImageURL)} alt={item.Name} className="product-image" />
                            <div className="product-info">
                                <h2>{item.Name}</h2>
                                <p>{item.Brand}</p>
                                <p>Rating: {item.Rating} ‚≠ê</p>
                                <p>{item.ReviewCount} reviews</p>
                                <div className="quantity-controls">
                <button onClick={() => handleRemoveFromCart(item.ProdID, item.Name)}>-</button>
                <span>{item.count}</span>
                <button onClick={() => handleIncreaseCount(item.ProdID, item.Name)}>+</button>
            </div>
                                <button className="remove-from-cart-button" onClick={() => handleRemoveFromCart(item.ProdID,item.Name,'remove')}>Remove</button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
            <button className="go-back-button" onClick={() => navigate('/')}>Go Back</button>
        </div>
    );
};

export default Cart;
