import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './ProductDetail.css';

const ProductDetail = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { product } = location.state || {};
  const [similarProducts, setSimilarProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    if (product) {
      const fetchSimilarProducts = async () => {
        setLoading(true);
        setError('');
        const description = product?.Description || ''; // Use product description to search for similar products
        try {
          const { data } = await axios.get(
            `http://localhost:5000/search-similar-products?description=${encodeURIComponent(description)}`
          );
          setSimilarProducts(data); // Ensure the API returns at least 10 similar products
        } catch (err) {
          setError('Failed to fetch similar products. Please try again.');
        } finally {
          setLoading(false);
        }
      };

      fetchSimilarProducts();
    }
  }, [product]);

  const getFirstValidImageUrl = (imageUrl) => {
    const imageUrls = imageUrl.split('|').map((url) => url.trim());
    for (const url of imageUrls) {
      if (url) return url; // Return the first valid URL
    }
    return 'https://via.placeholder.com/150'; // Default placeholder image
  };

  if (!product) {
    return <p>Product not found</p>;
  }

  return (
    <div className="product-detail-container">
      <div className="product-detail">
        <div className="image">
          <img
            src={getFirstValidImageUrl(product.ImageURL)}
            alt={product.Name}
            className="product-detail-image"
          />
          <button className="btn" id="addtoCart">Add to Cart</button>
          <button className="btn" id="buyNow">Buy Now</button>
        </div>

        <div className="product-detail-info">
          <h2>{product.Name}</h2>
          <p>{product.Brand}</p>
          <p>Rating: {product.Rating} ⭐</p>
          <p>{product.ReviewCount} reviews</p>
          <p>{product.Description}</p>
        </div>
      </div>

      <h3>Similar Products</h3>
      {loading && <p>Loading similar products...</p>}
      {error && <p className="error">{error}</p>}
      <div className="similar-products-container">
        {similarProducts.length > 0 ? (
          similarProducts.map((similarProduct, index) => (
            <div key={index} className="similar-product-card" onClick={() => navigate(`/product-detail`, { state: { product: similarProduct } })}>
              <img
                src={getFirstValidImageUrl(similarProduct.ImageURL)}
                alt={similarProduct.Name}
                className="similar-product-image"
              />
              <div className="similar-product-info">
                <h4>{similarProduct.Name}</h4>
                <p>{similarProduct.Brand}</p>
                <p>Rating: {similarProduct.Rating} ⭐</p>
                <p>{similarProduct.ReviewCount} reviews</p>
              </div>
            </div>
          ))
        ) : (
          <p>No similar products found.</p>
        )}
      </div>
    </div>
  );
};

export default ProductDetail;
