import React, { useState } from 'react';
import axios from 'axios';
import './Navbar.css'; // Ensure this file contains the styles

const Navbar = () => {
  const [productName, setProductName] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [userEmail, setUserEmail] = useState(localStorage.getItem('userEmail') || null); // Store email in state

  const fetchRecommendations = async () => {
    setLoading(true);
    setError('');
    try {
      const { data } = await axios.get(`http://localhost:5000/content-recommendation?item_name=${productName}`);
      if (Array.isArray(data)) {
        setRecommendations(data);
      } else {
        setError('Unexpected data format');
      }
    } catch (error) {
      setError('Failed to fetch recommendations. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    if (productName.trim()) {
      fetchRecommendations();
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userEmail');
    setUserEmail(null); // Clear email from state
  };

  const renderAccountInfo = () => {
    if (userEmail) {
      const displayName = userEmail.split('@')[0]; // Get the part before '@'
      const firstLetter = displayName.charAt(0).toUpperCase(); // First letter for the circle

      return (
        <div className="account-info" onClick={handleLogout}>
          <div className="email-circle">{firstLetter}</div>
          <span>{displayName}</span>
        </div>
      );
    }
    return <a href="/account">Account</a>; // Default link if not logged in
  };

  return (
    <div>
      <nav className="navbar">
        <div className="navbar-brand">
          <a href="/">Home</a>
        </div>
        <div className="search-bar-container">
          <input
            type="text"
            value={productName}
            onChange={(e) => setProductName(e.target.value)}
            placeholder="Search for products"
            className="search-input"
          />
          <button onClick={handleSearch} disabled={loading} className="search-button">
            {loading ? 'Loading...' : 'Search'}
          </button>
        </div>
        <div className="navbar-links">
          {renderAccountInfo()} {/* Display account info */}
        </div>
      </nav>
      {recommendations.length > 0 && (
        <div className="recommendation-container">
          {recommendations.map((rec) => (
            <div key={rec.Name} className="recommendation-card">
              <img src={rec.ImageURL} alt={rec.Name} className="product-image" />
              <div className="product-info">
                <h2>{rec.Name}</h2>
                <p>{rec.Brand}</p>
                <p>Rating: {rec.Rating} ⭐</p>
                <p>{rec.ReviewCount} reviews</p>
                <button className="add-to-cart-button">Add to Cart</button>
              </div>
            </div>
          ))}
        </div>
      )}
      {error && <p className="error">{error}</p>}
    </div>
  );
};

export default Navbar;
