import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Navbar.css';

const brandsList = [
  'Apple', 'Samsung', 'Sony', 'LG', 'Dell', 'HP', 'Asus', 'Lenovo', 'Nike', 'Adidas'
];

const Navbar = () => {
  const [productName, setProductName] = useState('');
  const [selectedBrands, setSelectedBrands] = useState([]);
  const [userEmail, setUserEmail] = useState(localStorage.getItem('userEmail') || '');
  const navigate = useNavigate();

  const handleSearch = () => {
    if (productName.trim()) {
      const brandsQuery = selectedBrands.length ? `&brands=${selectedBrands.join(',')}` : '';
      navigate(`/search?query=${encodeURIComponent(productName)}${brandsQuery}`);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userEmail');
    setUserEmail('');
    navigate('/');
  };

  const handleBrandChange = (e) => {
    const selectedOptions = Array.from(e.target.selectedOptions, (option) => option.value);
    setSelectedBrands(selectedOptions);
  };

  const getEmailDetails = (email) => {
    if (!email) return { initial: '', displayName: '' };
    const firstLetter = email[0].toUpperCase();
    const name = email.split('@')[0];
    return { initial: firstLetter, displayName: name };
  };

  const { initial, displayName } = getEmailDetails(userEmail);

  return (
    <div>
      <nav className="navbar">
        <div className="navbar-brand">
          <a href="/">Home</a>
        </div>

        {/* Scrollable Brand Selection */}
        <div className="brand-select-container">
          <label>Select Brands:</label>
          <select multiple value={selectedBrands} onChange={handleBrandChange} className="brand-select">
            {brandsList.map((brand) => (
              <option key={brand} value={brand}>{brand}</option>
            ))}
          </select>
        </div>

        {/* Search Bar */}
        <div className="search-bar-container">
          <input
            type="text"
            value={productName}
            onChange={(e) => setProductName(e.target.value)}
            placeholder="Search for products"
            className="search-input"
          />
          <button onClick={handleSearch} className="search-button">Search</button>
        </div>

        <div className="navbar-links">
          <a href="/cart">Cart</a>
          {userEmail ? (
            <div className="user-info">
              <div className="user-icon">{initial}</div>
              <button onClick={handleLogout} className="logout-button">Logout</button>
            </div>
          ) : (
            <a href="/account">Account</a>
          )}
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
