import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useHistory } from 'react-router-dom';
import './Home.css'; // Make sure to import your CSS file

const Home = ({ userId }) => {
    const [recommendedProducts, setRecommendedProducts] = useState([]);
    const history = useHistory();

    useEffect(() => {
        const fetchRecommendations = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/recommendations?user_id=${userId}`);
                setRecommendedProducts(response.data);
            } catch (error) {
                console.error('Error fetching recommendations:', error);
            }
        };

        if (userId) {
            fetchRecommendations();
        }
    }, [userId]);

    const handleCardClick = (prodID) => {
        // Navigate to the product detail page with the prodID as a parameter
        history.push(`/product/${prodID}`);
    };

    return (
        <div className="home">
            <h1>Recommended Products</h1>
            <div className="recommendation-container">
                {recommendedProducts.map((rec) => (
                    <div key={rec.ProdID} className="recommendation-card" onClick={() => handleCardClick(rec.ProdID)}>
                        <img src={rec.ImageURL} alt={rec.Name} className="product-image" />
                        <div className="product-info">
                            <h2>{rec.Name.length > 20 ? `${rec.Name.substring(0, 20)}...` : rec.Name}</h2>
                            <p>{rec.Brand}</p>
                            <p>Rating: {rec.Rating} ‚≠ê</p>
                            <p>{rec.ReviewCount} reviews</p>
                            <button className="add-to-cart-button">Add to Cart</button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
