import { useParams } from 'react-router-dom';
import { useState, useEffect } from 'react';

const ProductDetail = () => {
  const { prodId } = useParams(); // Get the product ID from the URL
  const [product, setProduct] = useState(null);

  useEffect(() => {
    // Fetch product data by ProdID (assuming data is stored in `data` array)
    const selectedProduct = data.find((item) => item.ProdID === parseInt(prodId));
    setProduct(selectedProduct);
  }, [prodId]);

  if (!product) return <div>Loading...</div>;

  return (
    <div className="product-detail">
      <img src={product.ImageURL} alt={product.Name} />
      <h2>{product.Name}</h2>
      <p>{product.Description}</p>
      <p>Category: {product.Category}</p>
      <p>Brand: {product.Brand}</p>
      <p>Rating: {product.Rating}</p>
      <p>Reviews: {product.ReviewCount}</p>
    </div>
  );
};

export default ProductDetail;
