import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';  // Use useNavigate instead of useHistory
import './Home.css';  // Create CSS for styling the product cards

const Home = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const navigate = useNavigate();  // Use navigate for programmatic navigation

  const getValidImageUrl = (imageUrl) => {
    const imageUrls = imageUrl.split('|').map(url => url.trim());
    for (const url of imageUrls) {
      if (url) return url;
    }
    return 'https://via.placeholder.com/150';
  };

  useEffect(() => {
    const fetchCollaborativeRecommendations = async () => {
      const userId = localStorage.getItem('user_id');
      if (!userId) return;

      setLoading(true);
      setError('');

      try {
        const { data } = await axios.get(`http://localhost:5000/recommendations?user_id=${userId}`);
        const filteredRecommendations = data.filter(rec => rec.Rating > 0.0);
        setRecommendations(filteredRecommendations);
      } catch (error) {
        setError('Failed to fetch recommendations. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchCollaborativeRecommendations();
  }, []);

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);  // Use navigate for redirection
  };

  return (
    <div className="home-container">
      <h1>Recommended Products for You</h1>

      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}
      {recommendations.length === 0 && !loading && !error && (
        <p>No recommendations found</p>
      )}
      
      <div className="recommendation-container">
        {recommendations.map((rec) => (
          <div 
            key={rec.Name} 
            className="recommendation-card"
            onClick={() => handleProductClick(rec.ProdID)}  // Handle the click event to navigate
          >
            <img 
              src={getValidImageUrl(rec.ImageURL)} 
              alt={rec.Name} 
              className="product-image" 
            />
            <div className="product-info">
              <h2>{rec.Name}</h2>
              <p>{rec.Brand}</p>
              <p>Rating: {rec.Rating} ‚≠ê</p>
              <p>{rec.ReviewCount} reviews</p>
              <button className="add-to-cart-button">Add to Cart</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
