import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom'; 
import axios from 'axios';
import './Search.css';
import Products from './Products';

const Search = ({ cart, setCart, handleAddToCart }) => {
  const [searchParams] = useSearchParams();
  const [recommendations, setRecommendations] = useState([]);
  const [hybridRecommendations, setHybridRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const productName = searchParams.get('query');
  const navigate = useNavigate();
  const userId = localStorage.getItem("user_id");  // Get userId from localStorage

  const handleCardClick = (product) => {
    navigate(`/product-detail?query=${product.Name}`, { state: { product } });
  };

  useEffect(() => {
    if (productName) {
      localStorage.setItem("last_searched_product", productName);  // Save searched product
    }
  }, [productName]);

  // Fetch Recommendations based on content
  useEffect(() => {
    const fetchRecommendations = async () => {
      if (!productName) return;
      setLoading(true);
      setError('');
      try {
        const { data } = await axios.get(`http://localhost:5000/content-recommendation?item_name=${productName}`);
        setRecommendations(data);
      } catch (error) {
        setError('Failed to fetch content recommendations. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchRecommendations();
  }, [productName]);

  // Fetch Hybrid Recommendations based on userId and last searched product
  useEffect(() => {
    const fetchHybridRecommendations = async () => {
      if (!userId) return;
      
      const lastSearchedProduct = localStorage.getItem("last_searched_product") || productName;

      if (!lastSearchedProduct) return;

      setLoading(true);
      setError('');
      try {
        const response = await axios.get('http://localhost:5000/hybrid-recommendation', {
          params: {
            user_id: userId,
            item_name: lastSearchedProduct, // Use last searched product or the current search term
          },
        });
        setHybridRecommendations(response.data);
      } catch (error) {
        setError('Failed to fetch hybrid recommendations. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchHybridRecommendations();
  }, [userId, productName]);

  return (
    <div className="search-container">
      <h1>Search Results for "{productName}"</h1>

      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}
      {recommendations.length === 0 && hybridRecommendations.length === 0 && !loading && !error && (
        <p>No recommendations found</p>
      )}

      {/* Show hybrid recommendations first, then content recommendations if no hybrid results */}
      <Products 
        items={hybridRecommendations.length > 0 ? hybridRecommendations : recommendations} 
        handleAddToCart={handleAddToCart} 
        handleClick={handleCardClick} 
      />
    </div>
  );
};

export default Search;
