import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Home.css';
import Products from './Products';

const Home = ({ cart, setCart, handleAddToCart }) => {
  const [recommendations, setRecommendations] = useState([]);
  const [topRated, setTopRated] = useState([]);
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [hybridRecommendations, setHybridRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState(0);
  const [searchItem, setSearchItem] = useState('');

  const navigate = useNavigate();
  const userId = localStorage.getItem("user_id");

  const handleSearchChange = (e) => {
    setSearchItem(e.target.value);
  };

  const handleSearchSubmit = () => {
    localStorage.setItem("last_searched_product", searchItem);
    fetchHybridRecommendations(); // Trigger hybrid recommendations on search
  };

  const fetchCart = async () => {
    try {
      if (!userId) return;
      const response = await fetch(`http://localhost:5000/get_cart?user_id=${userId}`);
      const data = await response.json();
      if (response.ok) {
        setCart(data.cart);
      }
    } catch (error) {
      console.error("Error fetching cart:", error);
    }
  };

  useEffect(() => {
    const fetchRelatedProducts = async () => {
      try {
        if (!userId) return;
        const response = await fetch(`http://localhost:5000/recommend-by-brand?user_id=${userId}`);
        const data = await response.json();
        if (response.ok) {
          setRelatedProducts(data?.slice(0, 15));
        }
      } catch (error) {
        console.error("Error fetching related products:", error);
      }
    };

    const fetchCollaborativeRecommendations = async () => {
      try {
        if (!userId) return;
        setLoading(true);
        const { data } = await axios.get(`http://localhost:5000/recommendations?user_id=${userId}`);
        setRecommendations(data.filter(rec => rec.Rating > 0.0));
      } catch (error) {
        setError('Failed to fetch recommendations.');
      } finally {
        setLoading(false);
      }
    };

    const fetchTopRatedProducts = async () => {
      try {
        setLoading(true);
        const { data } = await axios.get('http://localhost:5000/rating-recommendation');
        setTopRated(data);
      } catch (error) {
        setError('Failed to fetch top-rated products.');
      } finally {
        setLoading(false);
      }
    };

    // **Hybrid Recommendations Logic** (Using `userId` and last searched item)
    const fetchHybridRecommendations = async () => {
      try {
        if (!userId) return;
        
        const lastSearchedProduct = localStorage.getItem("last_searched_product") || "shampoo"; // Default to "shampoo" if none exists

        setLoading(true);
        const response = await axios.get('http://localhost:5000/hybrid-recommendation', {
          params: {
            user_id: userId,
            item_name: searchItem || lastSearchedProduct, // Use searchItem or last searched item
          }
        });

        if (response.status === 200) {
          setHybridRecommendations(response.data);
        }
      } catch (error) {
        setError('Failed to fetch hybrid recommendations.');
      } finally {
        setLoading(false);
      }
    };

    fetchRelatedProducts();
    fetchCollaborativeRecommendations();
    fetchTopRatedProducts();
    fetchHybridRecommendations();
    fetchCart();
  }, [userId, searchItem]);

  const handleCardClick = (product) => {
    navigate(`/product-detail?query=${product.Name}`, { state: { product } });
  };

  const renderProducts = () => {
    if (activeTab === 0) {
      return <Products items={recommendations} handleAddToCart={handleAddToCart} handleClick={handleCardClick} />;
    } else if (activeTab === 1) {
      return <Products items={relatedProducts} handleAddToCart={handleAddToCart} handleClick={handleCardClick} />;
    } else if (activeTab === 2) {
      return <Products items={topRated} handleAddToCart={handleAddToCart} handleClick={handleCardClick} />;
    } else if (activeTab === 3) {
      return <Products items={hybridRecommendations} handleAddToCart={handleAddToCart} handleClick={handleCardClick} />;
    }
  };

  return (
    <div className="home-container">
      <div className="search-container">
        <input
          type="text"
          value={searchItem}
          onChange={handleSearchChange}
          placeholder="Search for products"
          className="search-input"
        />
        <button onClick={handleSearchSubmit} className="search-button">Search</button>
      </div>

      <div className="tab-buttons flex gap-4 justify-center my-6">
        <button className={`px-4 py-2 rounded ${activeTab === 0 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`} onClick={() => setActiveTab(0)}>
          Recommended
        </button>
        <button className={`px-4 py-2 rounded ${activeTab === 1 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`} onClick={() => setActiveTab(1)}>
          Related to You
        </button>
        <button className={`px-4 py-2 rounded ${activeTab === 2 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`} onClick={() => setActiveTab(2)}>
          Top Rated
        </button>
        <button className={`px-4 py-2 rounded ${activeTab === 3 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`} onClick={() => setActiveTab(3)}>
          Smart Recommendations
        </button>
      </div>

      {loading && <p className="text-center">Loading...</p>}
      {error && <p className="text-center text-red-500">{error}</p>}
      {!loading && renderProducts()}

      <hr className="section-divider" />
      <footer className="footer">
        <p>&copy; 2025 Rahilkhan Recommendation system. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
