import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminDashboard = () => {
  const [topProducts, setTopProducts] = useState([]);
  const [topBrands, setTopBrands] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:5000/admin-dashboard-data")
      .then((res) => {
        setTopProducts(res.data.top_products || []); // prevent undefined
        setTopBrands(res.data.top_brands || []);
      })
      .catch((err) => {
        console.error("Error fetching admin data:", err);
      });
  }, []);

  return (
    <div className="admin-dashboard">
      <h2>Top Rated Products</h2>
      <div className="product-cards">
        {topProducts.map((product) => (
          <div key={product.ProdID} className="card">
            <img src={product.ImageURL} alt={product.Name} />
            <h3>{product.Name}</h3>
            <p><strong>Brand:</strong> {product.Brand}</p>
            <p><strong>Rating:</strong> {product.Rating}</p>
          </div>
        ))}
      </div>

      <h2>Top Brands by Average Rating</h2>
      <ul>
        {topBrands.map((brand, index) => (
          <li key={index}>
            <strong>{brand.Brand}</strong> - Avg Rating: {brand.AverageRating.toFixed(2)}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
