import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Navbar from './components/Navbar';
import Home from './components/Home';
import Cart from './components/Cart';
import SignIn from './components/SignIn';
import Login from './components/Login';
import Account from './components/Account';
import axios from 'axios';

const App = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchRecommendations = async (productName) => {
    setLoading(true);
    setError('');
    try {
      const { data } = await axios.get(`http://localhost:5000/content-recommendation?item_name=${productName}`);
      if (Array.isArray(data)) {
        setRecommendations(data);
      } else {
        setError('Unexpected data format');
      }
    } catch (err) {
      setError('Failed to fetch recommendations. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <Router>
      <Navbar fetchRecommendations={fetchRecommendations} />
      <Routes>
        <Route 
          path="/" 
          element={<Home recommendations={recommendations} loading={loading} error={error} />} 
        />
        <Route path="/cart" element={<Cart />} />
        <Route path="/signin" element={<SignIn />} />
        <Route path="/login" element={<Login />} />
        <Route path="/account" element={<Account />} />
      </Routes>
    </Router>
  );
};

export default App;
