// ProductDetail.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './ProductDetail.css'; // Add styles for the product detail page

const ProductDetail = () => {
    const { prodID } = useParams();
    const [product, setProduct] = useState(null);
    const [recommendedProducts, setRecommendedProducts] = useState([]);

    useEffect(() => {
        const fetchProductDetails = async () => {
            try {
                // Fetch product details based on prodID
                const response = await axios.get(`http://localhost:5000/product/${prodID}`); // Update the endpoint as necessary
                setProduct(response.data);
            } catch (error) {
                console.error('Error fetching product details:', error);
            }
        };

        const fetchRecommendations = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/recommendations?user_id=${userId}`); // Update with user ID as necessary
                setRecommendedProducts(response.data);
            } catch (error) {
                console.error('Error fetching recommendations:', error);
            }
        };

        fetchProductDetails();
        fetchRecommendations();
    }, [prodID]);

    if (!product) return <div>Loading...</div>;

    return (
        <div className="product-detail">
            <h1>{product.Name}</h1>
            <img src={product.ImageURL} alt={product.Name} className="product-image" />
            <p>{product.Description}</p>
            <p>Brand: {product.Brand}</p>
            <p>Rating: {product.Rating} ⭐</p>
            <p>{product.ReviewCount} reviews</p>
            <button className="add-to-cart-button">Add to Cart</button>

            <h2>Recommended Products</h2>
            <div className="recommendation-container">
                {recommendedProducts.map((rec) => (
                    <div key={rec.ProdID} className="recommendation-card">
                        <img src={rec.ImageURL} alt={rec.Name} className="product-image" />
                        <div className="product-info">
                            <h2>{rec.Name.length > 20 ? `${rec.Name.substring(0, 20)}...` : rec.Name}</h2>
                            <p>{rec.Brand}</p>
                            <p>Rating: {rec.Rating} ⭐</p>
                            <p>{rec.ReviewCount} reviews</p>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default ProductDetail;
