// src/components/Products.js
export default function Products({ items = [], handleAddToCart, handleClick }) {
  const getValidImageUrl = (imageUrl) => {
    const imageUrls = imageUrl.split('|').map(url => url.trim());
    for (const url of imageUrls) {
      if (url) return url;
    }
    return 'https://via.placeholder.com/150'; 
  };

  if (!Array.isArray(items)) {
    return <div className="text-center p-5 text-red-500">Invalid product data.</div>;
  }

  return (
    <div className="product-grid">
      {items.length > 0 ? (
        items.map((item) => (
          <div 
            key={item.ProdID} 
            className="product-card transition-transform transform hover:scale-105" 
            onClick={() => { handleClick(item); }}
          >
            <img src={getValidImageUrl(item.ImageURL)} alt={item.Name} className="product-image" />
            <div className="product-info">
              <h2>{item.Name}</h2>
              <p>{item.Brand}</p>
              <p>Rating: {item.Rating} ‚≠ê</p>
              <p>{item.ReviewCount ? `${item.ReviewCount} reviews` : 'No reviews yet'}</p>
              <button 
                className="add-to-cart-button p-2 m-2 mx-8"
                onClick={(e) => { e.stopPropagation(); handleAddToCart(item); }}
              >
                Add to Cart
              </button>
            </div>
          </div>
        ))
      ) : (
        <p className="text-center">No products available.</p>
      )}
    </div>
  );
}
