// ProductDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // For accessing dynamic params in URL
import axios from 'axios';
import './ProductDetail.css'; // Optional: Add styling for product details

const ProductDetail = () => {
  const { productId } = useParams(); // Get the product ID from URL params
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchProductDetails = async () => {
      setLoading(true);
      try {
        const response = await axios.get(`http://localhost:5000/product/${productId}`);
        setProduct(response.data);
      } catch (err) {
        setError('Failed to fetch product details');
      } finally {
        setLoading(false);
      }
    };

    fetchProductDetails();
  }, [productId]);

  if (loading) return <p>Loading product details...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="product-detail-container">
      {product && (
        <div className="product-detail">
          <img 
            src={product.ImageURL || 'https://via.placeholder.com/150'} 
            alt={product.Name} 
            className="product-detail-image" 
          />
          <div className="product-detail-info">
            <h1>{product.Name}</h1>
            <p>{product.Description}</p>
            <p><strong>Brand:</strong> {product.Brand}</p>
            <p><strong>Rating:</strong> {product.Rating} ‚≠ê</p>
            <p><strong>Price:</strong> ${product.Price}</p>
            <button className="add-to-cart-button">Add to Cart</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default ProductDetail;
