import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AdminDashboard.css'; // Optional styling

const AdminDashboard = () => {
  const [topProducts, setTopProducts] = useState([]);
  const [topBrands, setTopBrands] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get('http://localhost:5000/admin-dashboard-data');
      setTopProducts(response.data.top_products);
      setTopBrands(response.data.top_brands);
    };
    fetchData();
  }, []);

  return (
    <div className="admin-dashboard">
      <h2>Top Rated Products</h2>
      <div className="product-list">
        {topProducts.map(product => (
          <div key={product.prodID} className="product-card">
            <img src={product.ImageURL} alt={product.name} />
            <h4>{product.name}</h4>
            <p>Brand: {product.Brand}</p>
            <p>Rating: {product.Rating}</p>
          </div>
        ))}
      </div>

      <h2>Top Brands by Average Rating</h2>
      <ul className="brand-list">
        {topBrands.map((brand, index) => (
          <li key={index}>
            <strong>{brand.Brand}</strong> - Average Rating: {brand.average_rating.toFixed(2)}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminDashboard;
