import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [productId, setProductId] = useState('');
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchRecommendations = async () => {
    setLoading(true);
    setError('');
    try {
      const { data } = await axios.get(`/content-recommendation?product_id=${productId}`);
      setRecommendations(data);
    } catch (error) {
      setError('Failed to fetch recommendations. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <input
        type="text"
        value={productId}
        onChange={(e) => setProductId(e.target.value)}
        placeholder="Enter product ID"
      />
      <button onClick={fetchRecommendations} disabled={loading || !productId}>
        {loading ? 'Loading...' : 'Get Recommendations'}
      </button>

      {error && <p className="error">{error}</p>}

      {recommendations.length === 0 && !loading && !error && (
        <p>No recommendations found</p>
      )}

      <div>
        {recommendations.map((rec) => (
          <div key={rec.product_id}>
            <h2>{rec.ProdName}</h2>
            <p>{rec.ProdDescr}</p>
            <p>Price: ${rec.APrice}</p>
            <img src={rec.img_link} alt={rec.ProdName} />
          </div>
        ))}
      </div>
    </div>
  );
};

export default App;
