import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for navigation
import './Navbar.css';

const Navbar = () => {
  const [productName, setProductName] = useState('');
  const [userEmail, setUserEmail] = useState(localStorage.getItem('userEmail') || '');
  const [showBrands, setShowBrands] = useState(false); // State for dropdown menu
  const navigate = useNavigate();

  const handleSearch = () => {
    if (productName.trim()) {
      navigate(`/search?query=${encodeURIComponent(productName)}`);
    }
  };

  const handleLogout = () => {
    // Clear the localStorage and state on logout
    localStorage.removeItem('token');
    localStorage.removeItem('userEmail');
    setUserEmail('');
    navigate('/'); // Redirect to the home page
  };

  const getEmailDetails = (email) => {
    if (!email) return { initial: '', displayName: '' };
    const firstLetter = email[0].toUpperCase();
    const name = email.split('@')[0];  
    return { initial: firstLetter, displayName: name };
  };

  const { initial, displayName } = getEmailDetails(userEmail);

  return (
    <div>
      <nav className="navbar">
        <div className="navbar-brand">
          <a href="/">Home</a>
        </div>

        {/* Brand Dropdown Menu */}
        <div className="brand-dropdown" onMouseEnter={() => setShowBrands(true)} onMouseLeave={() => setShowBrands(false)}>
          <button className="brand-button">Brands â–¼</button>
          {showBrands && (
            <ul className="dropdown-menu">
              <li><a href="/brand/opi">Opi</a></li>
              <li><a href="/brand/adidas">Adidas</a></li>
              <li><a href="/brand/puma">Puma</a></li>
              <li><a href="/brand/reebok">Reebok</a></li>
            </ul>
          )}
        </div>

        <div className="search-bar-container">
          <input
            type="text"
            value={productName}
            onChange={(e) => setProductName(e.target.value)}
            placeholder="Search for products"
            className="search-input"
          />
          <button onClick={handleSearch} className="search-button">
            Search
          </button>
        </div>

        <div className="navbar-links">
          <a href="/cart">Cart</a>
          {userEmail ? (
            <div className="user-info">
              <div className="user-icon">{initial}</div>
              <button onClick={handleLogout} className="logout-button">Logout</button>
            </div>
          ) : (
            <a href="/account">Account</a>
          )}
        </div>
      </nav>
    </div>
  );
};

export default Navbar;
