import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate, useLocation } from 'react-router-dom';  // Added useLocation
import './Home.css';
import Products from './Products';

const Home = ({ cart, setCart, handleAddToCart }) => {
  const [recommendations, setRecommendations] = useState([]);
  const [topRated, setTopRated] = useState([]);
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [smartRecommendations, setSmartRecommendations] = useState([]); // NEW state
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState(0);

  const navigate = useNavigate();
  const location = useLocation(); // Access last search item
  const userId = localStorage.getItem("user_id");

  // Extract last search item from location state (if available)
  const lastSearchItem = location.state?.lastSearchItem || "";

  const fetchRelatedProducts = async () => {
    try {
      if (!userId) return;
      const response = await fetch(`http://localhost:5000/recommend-by-brand?user_id=${userId}`);
      const data = await response.json();
      if (response.ok) {
        const relatedItems = data?.slice(0, 15);
        setRelatedProducts(relatedItems);
      }
    } catch (error) {
      console.error("Error fetching related products:", error);
    }
  };

  const fetchCollaborativeRecommendations = async () => {
    try {
      if (!userId) return;
      setLoading(true);
      const { data } = await axios.get(`http://localhost:5000/recommendations?user_id=${userId}`);
      const filtered = data.filter(rec => rec.Rating > 0.0);
      setRecommendations(filtered);
    } catch (error) {
      setError('Failed to fetch recommendations.');
    } finally {
      setLoading(false);
    }
  };

  const fetchTopRatedProducts = async () => {
    try {
      setLoading(true);
      const { data } = await axios.get('http://localhost:5000/rating-recommendation');
      setTopRated(data);
    } catch (error) {
      setError('Failed to fetch top-rated products.');
    } finally {
      setLoading(false);
    }
  };

  const fetchSmartRecommendations = async () => {
    try {
      if (!userId || !lastSearchItem) return;
      setLoading(true);
      const { data } = await axios.get(`http://localhost:5000/hybrid-recommendation?user_id=${userId}&item_name=${lastSearchItem}`);
      setSmartRecommendations(data);
    } catch (error) {
      setError('Failed to fetch smart recommendations.');
    } finally {
      setLoading(false);
    }
  };

  const fetchCart = async () => {
    try {
      if (!userId) return;
      const response = await fetch(`http://localhost:5000/get_cart?user_id=${userId}`);
      const data = await response.json();
      if (response.ok) {
        setCart(data.cart);
      }
    } catch (error) {
      console.error("Error fetching cart:", error);
    }
  };

  useEffect(() => {
    fetchRelatedProducts();
    fetchCollaborativeRecommendations();
    fetchTopRatedProducts();
    fetchSmartRecommendations();
    fetchCart();
  }, [userId, lastSearchItem]);  // Important: dependency updated!

  const handleCardClick = (product) => {
    navigate(`/product-detail?query=${product.Name}`, { state: { product } });
  };

  const renderProducts = () => {
    if (activeTab === 0) {
      return <Products items={recommendations} handleAddToCart={handleAddToCart} fetchCart={fetchCart} handleClick={handleCardClick} />;
    } else if (activeTab === 1) {
      return <Products items={relatedProducts} handleAddToCart={handleAddToCart} fetchCart={fetchCart} handleClick={handleCardClick} />;
    } else if (activeTab === 2) {
      return <Products items={topRated} handleAddToCart={handleAddToCart} fetchCart={fetchCart} handleClick={handleCardClick} />;
    } else if (activeTab === 3) {
      return <Products items={smartRecommendations} handleAddToCart={handleAddToCart} fetchCart={fetchCart} handleClick={handleCardClick} />;
    }
  };

  return (
    <div className="home-container">
      <div className="tab-buttons flex gap-4 justify-center my-6">
        <button className={`px-4 py-2 rounded ${activeTab === 0 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`} onClick={() => setActiveTab(0)}>Recommended</button>
        <button className={`px-4 py-2 rounded ${activeTab === 1 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`} onClick={() => setActiveTab(1)}>Related to You</button>
        <button className={`px-4 py-2 rounded ${activeTab === 2 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`} onClick={() => setActiveTab(2)}>Top Rated</button>
        <button className={`px-4 py-2 rounded ${activeTab === 3 ? 'bg-green-500 text-white' : 'bg-gray-200'}`} onClick={() => setActiveTab(3)}>Smart Recommendations</button> {/* New Button */}
      </div>

      {loading && <p className="text-center">Loading...</p>}
      {error && <p className="text-center text-red-500">{error}</p>}
      {!loading && renderProducts()}

      <hr className="section-divider" />
      <footer className="footer">
        <p>&copy; 2025 Rahilkhan Recommendation system. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
