import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Home.css';  // Create CSS for styling the product cards

const Home = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCollaborativeRecommendations = async () => {
      const userId = localStorage.getItem('user_id'); // Get the user_id from localStorage
      if (!userId) return;
    
      setLoading(true);
      setError('');
    
      try {
        const { data } = await axios.get(`http://localhost:5000/recommendations?user_id=${userId}`);
        console.log(data); // Log the response to see its structure
        
        // Ensure data is an array and filter out products with a rating of 0 or no reviews
        const filteredRecommendations = data.filter(rec => rec.Rating > 0.0 && rec.ReviewCount > 0);
        
        setRecommendations(filteredRecommendations);
      } catch (error) {
        setError('Failed to fetch recommendations. Please try again.');
      } finally {
        setLoading(false);
      }
    };
    

    fetchCollaborativeRecommendations();
  }, []);

  return (
    <div className="home-container">
      <h1>Recommended Products for You</h1>
      
      {loading && <p>Loading...</p>}
      {error && <p className="error">{error}</p>}
      {recommendations.length === 0 && !loading && !error && (
        <p>No recommendations found</p>
      )}
      <div className="recommendation-container">
  {Array.isArray(recommendations) && recommendations.length > 0 ? (
    recommendations.map((rec) => (
      <div key={rec.Name} className="recommendation-card">
        <img src={rec.ImageURL.split('|')[0].trim()} alt={rec.Name} className="product-image" />
        <div className="product-info">
          <h2>{rec.Name}</h2>
          <p>{rec.Brand}</p>
          <p>Rating: {rec.Rating} ‚≠ê</p>
          <p>{rec.ReviewCount} reviews</p>
          <button className="add-to-cart-button">Add to Cart</button>
        </div>
      </div>
    ))
  ) : (
    <p>No recommendations found</p>
  )}
</div>

    </div>
  );
};

export default Home;
