// src/components/HomePage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './HomePage.css'; // Add your styling here

const HomePage = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchRecommendations = async () => {
      const user_id = localStorage.getItem('user_id'); // Get the user_id from localStorage
      if (user_id) {
        try {
          const response = await axios.get(`http://localhost:5000/recommendations?user_id=${user_id}`);
          setRecommendations(response.data.recommendations); // Assuming your Flask backend returns 'recommendations'
        } catch (err) {
          setError('Error fetching recommendations');
        }
      }
    };

    fetchRecommendations();
  }, []);

  return (
    <div className="home-container">
      <h1>Welcome to the Home Page</h1>
      <h2>Recommended Products for You</h2>

      {error && <p className="error">{error}</p>}

      <div className="recommendation-list">
        {recommendations.length > 0 ? (
          recommendations.map((item) => (
            <div key={item.Name} className="recommendation-card">
              <img src={item.ImageURL} alt={item.Name} />
              <h3>{item.Name}</h3>
              <p>Brand: {item.Brand}</p>
              <p>Rating: {item.Rating} â˜…</p>
              <p>{item.ReviewCount} reviews</p>
            </div>
          ))
        ) : (
          <p>No recommendations available</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
