import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Home.css'; // Make sure to import your CSS file
 

const Home = () => {
    const [recommendations, setRecommendations] = useState([]);
    const [userId, setUserId] = useState(null); // Initialize userId state

    useEffect(() => {
        // Fetch the user ID from local storage or context after login
        const loggedInUserId = localStorage.getItem('user_id'); // Example of getting user ID from local storage
        if (loggedInUserId) {
            setUserId(loggedInUserId);
            fetchRecommendations(loggedInUserId);
        }
    }, []);

    const fetchRecommendations = async (user_id) => {
        try {
            const response = await fetch(`http://localhost:5000/collaborative-recommendation?user_id=${user_id}`);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            const data = await response.json();
            setRecommendations(data);
        } catch (error) {
            console.error('Error fetching recommendations:', error);
        }
    };

    return (
        <div>
            <h1>Recommendations</h1>
            <div className="recommendation-cards">
                {recommendations.map((item) => (
                    <div className="card" key={item.Name}>
                        <img src={item.ImageURL} alt={item.Name} />
                        <h2>{item.Name}</h2>
                        <p>{item.Brand}</p>
                        <p>Rating: {item.Rating}</p>
                        <p>Review Count: {item.ReviewCount}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Home;
