// src/components/Home.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './Home.css';
import Products from './Products';

const Home = ({ cart, setCart, handleAddToCart }) => {
  const [recommendations, setRecommendations] = useState([]);
  const [topRated, setTopRated] = useState([]);
  const [relatedProducts, setRelatedProducts] = useState([]);
  const [hybridRecommendations, setHybridRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [activeTab, setActiveTab] = useState(0);

  const navigate = useNavigate();
  const userId = localStorage.getItem('user_id');

  useEffect(() => {
    const fetchRelatedProducts = async () => {
      try {
        if (!userId) return;
        const response = await fetch(`http://localhost:5000/recommend-by-brand?user_id=${userId}`);
        const data = await response.json();
        if (response.ok) {
          setRelatedProducts(data?.slice(0, 15));
        }
      } catch (error) {
        console.error('Error fetching related products:', error);
      }
    };

    const fetchCollaborativeRecommendations = async () => {
      try {
        if (!userId) return;
        setLoading(true);
        const { data } = await axios.get(`http://localhost:5000/recommendations?user_id=${userId}`);
        setRecommendations(data.filter((rec) => rec.Rating > 0.0));
      } catch (error) {
        setError('Failed to fetch recommendations.');
      } finally {
        setLoading(false);
      }
    };

    const fetchTopRatedProducts = async () => {
      try {
        setLoading(true);
        const { data } = await axios.get('http://localhost:5000/rating-recommendation');
        setTopRated(data);
      } catch (error) {
        setError('Failed to fetch top-rated products.');
      } finally {
        setLoading(false);
      }
    };

    const fetchHybridRecommendations = async () => {
      try {
        if (!userId) return;
    
        const lastSearchedProduct = localStorage.getItem('last_searched_product') || 'shampoo'; // fallback if empty
    
        setLoading(true);
        const response = await axios.get('http://localhost:5000/hybrid-recommendation', {
          params: {
            user_id: userId,
            item_name: lastSearchedProduct,
            top_n: 10,
          },
        });
    
        console.log('Hybrid Recommendations Response:', response.data); // Log to inspect the structure
    
        // Assuming response.data is not an array, check if it's an object
        if (Array.isArray(response.data)) {
          // If it's an array, proceed as before
          setHybridRecommendations({
            recommendations: response.data.map((item) => ({
              productId: item.prodID,
              productName: item.name,
              productDescription: item.description,
              productImage: item.ImageURL,
              rating: item.Rating,
            })),
          });
        } else {
          // Handle if the response is not an array (for example, an object)
          setHybridRecommendations({
            recommendations: [] // or handle other cases based on the actual structure
          });
          console.error('Received data is not an array:', response.data);
        }
      } catch (error) {
        setError('Failed to fetch hybrid recommendations.');
        console.error('Error fetching hybrid recommendations:', error);
      } finally {
        setLoading(false);
      }
    };

    // Call all the necessary functions
    fetchRelatedProducts();
    fetchCollaborativeRecommendations();
    fetchTopRatedProducts();
    fetchHybridRecommendations();
  }, [userId]);

  const fetchCart = async () => {
    try {
      if (!userId) return;
      const response = await fetch(`http://localhost:5000/get_cart?user_id=${userId}`);
      const data = await response.json();
      if (response.ok) {
        setCart(data.cart);
      }
    } catch (error) {
      console.error('Error fetching cart:', error);
    }
  };

  useEffect(() => {
    fetchCart();
  }, []);

  const handleCardClick = (product) => {
    navigate(`/product-detail?query=${product.Name}`, { state: { product } });
  };

  const renderProducts = () => {
    if (activeTab === 0) {
      return (
        <Products
          items={recommendations}
          handleAddToCart={handleAddToCart}
          fetchCart={fetchCart}
          handleClick={handleCardClick}
        />
      );
    } else if (activeTab === 1) {
      return (
        <Products
          items={relatedProducts}
          handleAddToCart={handleAddToCart}
          fetchCart={fetchCart}
          handleClick={handleCardClick}
        />
      );
    } else if (activeTab === 2) {
      return (
        <Products
          items={topRated}
          handleAddToCart={handleAddToCart}
          fetchCart={fetchCart}
          handleClick={handleCardClick}
        />
      );
    } else if (activeTab === 3) {
      return (
        <Products
          items={hybridRecommendations.recommendations}
          handleAddToCart={handleAddToCart}
          fetchCart={fetchCart}
          handleClick={handleCardClick}
        />
      );
    }
  };

  return (
    <div className="home-container">
      <div className="tab-buttons flex gap-4 justify-center my-6">
        <button
          className={`px-4 py-2 rounded ${activeTab === 0 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
          onClick={() => setActiveTab(0)}
        >
          Recommended
        </button>
        <button
          className={`px-4 py-2 rounded ${activeTab === 1 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
          onClick={() => setActiveTab(1)}
        >
          Related to You
        </button>
        <button
          className={`px-4 py-2 rounded ${activeTab === 2 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
          onClick={() => setActiveTab(2)}
        >
          Top Rated
        </button>
        <button
          className={`px-4 py-2 rounded ${activeTab === 3 ? 'bg-blue-500 text-white' : 'bg-gray-200'}`}
          onClick={() => setActiveTab(3)}
        >
          Smart Recommendations
        </button>
      </div>

      {loading && <p className="text-center">Loading...</p>}
      {error && <p className="text-center text-red-500">{error}</p>}
      {!loading && renderProducts()}

      <hr className="section-divider" />
      <footer className="footer">
        <p>&copy; 2025 Rahilkhan Recommendation system. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
