import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Cart.css';

const Cart = ({cart,setCart}) => {
    // const [cart, setCart] = useState([]);

    // useEffect(() => {
    //     const storedCart = JSON.parse(localStorage.getItem('cart')) || [];
    //     setCart(storedCart);
    // }, []);

    const handleRemoveFromCart = (productId) => {
        const updatedCart = cart.filter(item => item.ProdID !== productId);
        setCart(updatedCart);
        localStorage.setItem('cart', JSON.stringify(updatedCart));
    };

    const navigate = useNavigate();

    const fetchCart = async () => {
        try {
          const userId = localStorage.getItem('user_id');
    
            if (!userId) return;
    
            const response = await fetch(`http://localhost:5000/get_cart?user_id=${userId}`);
            const data = await response.json();
    
            if (response.ok) {
                setCart(data.cart);  // Update cart state
            } else {
                console.error("Error fetching cart:", data.error);
            }
        } catch (error) {
            console.error("Error fetching cart:", error);
        }
    };
    
    // Call fetchCart when the component mounts
    useEffect(() => {
        fetchCart();
    }, [cart]);

    return (
        <div className="cart-container">
            <h1>Your Cart</h1>
            {cart.length === 0 ? (
                <p>Your cart is empty.</p>
            ) : (
                <div className="cart-items">
                    {cart.map((item) => (
                        <div key={item.ProdID} className="cart-item">
                            <img src={item.ImageURL} alt={item.Name} className="cart-image" />
                            <div className="cart-info">
                                <h2>{item.Name}</h2>
                                <p>{item.Brand}</p>
                                <p>Rating: {item.Rating} ‚≠ê</p>
                                <p>{item.ReviewCount} reviews</p>
                                <button className="remove-from-cart-button" onClick={() => handleRemoveFromCart(item.ProdID)}>Remove</button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
            <button className="go-back-button" onClick={() => navigate('/')}>Go Back</button>
        </div>
    );
};

export default Cart;
