import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from './Navbar'; // Ensure Navbar is in the same directory
import './HomePage.css'; // Ensure you have appropriate styling

const Home = () => {
  const [recommendations, setRecommendations] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchRecommendations = async () => {
      setLoading(true);
      setError('');
      try {
        const { data } = await axios.get('http://localhost:5000/rating-recommendation');
        setRecommendations(data);
      } catch (error) {
        setError('Failed to fetch recommendations. Please try again.');
      } finally {
        setLoading(false);
      }
    };

    fetchRecommendations();
  }, []);

  return (
    <div>
      <Navbar />
      <div className="home-content">
        <h1>Welcome to Our Store!</h1>
        {loading && <p>Loading recommendations...</p>}
        {error && <p className="error">{error}</p>}
        {recommendations.length === 0 && !loading && !error && (
          <p>No recommendations available</p>
        )}
        <div className="recommendation-container">
          {recommendations.map((rec) => (
            <div key={rec.Name} className="recommendation-card">
              <img src={rec.ImageURL} alt={rec.Name} className="product-image" />
              <div className="product-info">
                <h2>{rec.Name}</h2>
                <p>{rec.Brand}</p>
                <p>Rating: {rec.Rating} ‚≠ê</p>
                <p>{rec.ReviewCount} reviews</p>
                <button className="add-to-cart-button">Add to Cart</button>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Home;
