import React from "react";

const Products = ({ products }) => {
  return (
    <div className="product-container">
      {products && products.length > 0 ? (
        products.map((product, index) => (
          <div key={index} className="product-card">
            <div className="product-images">
              {/* Display the first image from the list of images */}
              {product.ImageURL && product.ImageURL.split(" | ").map((image, imgIndex) => (
                <img key={imgIndex} src={image} alt={product.Name} />
              ))}
            </div>
            <h3>{product.Name}</h3>
            <p>Brand: {product.Brand}</p>
            <p>Rating: {product.Rating}</p>
            <p>Review Count: {product.ReviewCount}</p>
          </div>
        ))
      ) : (
        <p>Invalid product data.</p>
      )}
    </div>
  );
};

export default Products;
